ToolBarButton.ClientEvent:
var dataTree = this.get("#dtSystems");
var NodeType = dataTree.get("currentNode.bindingConfig.name");
var system = "";
var feature = "";
var id = "";
if(NodeType == "system"){
	system = view.id("dsSystems").getData("!CURRENT_SYSTEM").get("system");
	feature = view.id("dsSystems").getData("!CURRENT_SYSTEM").get("system");
	id = view.id("dsSystems").getData("!CURRENT_SYSTEM").get("oid");
	
}
else if(NodeType == "feature"){
	system = view.id("dsSystems").getData("!CURRENT_FEATURE").get("system.system");
	feature = view.id("dsSystems").getData("!CURRENT_FEATURE").get("feature");
	id = view.id("dsSystems").getData("!CURRENT_FEATURE").get("oid");
}
view.id("dsRoad").insert({
	system:system,
	feature:feature,
	id:id
});
view.id("diaRoad").show();

var cur = view.id('dsRoad').getData('#');
var ua = view.id('uaRoad');
if(cur){
		dorado.MessageBox.confirm('您是否确认删除此故障传播路径?', function(){	
		cur.remove();
		ua.execute();	
		}); 
}

var cur = view.id("dsRoad").getData("#");
if(cur){
var j = cur.get("fault").toJSON();
if(j.length > 0){
var event = "";
var classes = "";
for(var i=0; i<j.length; i++){
	if(j[i].parentnode == null){
		event = j[i].event;
		classes = j[i].classes;
	}
}
view.id("dsRepairresult").set("parameter",{
	event:event,
	location:classes
});
view.id("dsRepairresult").flush();
var result = view.id("dsRepairresult").getData("#");
if(result){
	result.set("id",cur.get("roadid"));
	view.id("diarepair").show();
}else{
	view.id("dsRepairresult").insert({
		id:cur.get("roadid"),
		event:event,
		location:classes
	});
	view.id("diarepair").show();
}
}
}

