		category: "LRU",
		fault:farr[i].get("event"),
		name:farr[i].get("classes"),
		loc:farr[i].get("origin"),
		key:farr[i].get("road")
	});
}
var line = [];
for(var i= 1; i < farr.length; i++){
	line.push({
		from: i-1,
		to: i
	});
}
var json = { "class": "go.GraphLinksModel",
  			 "nodeDataArray": arr,
  			 "linkDataArray": line
			}
myDiagram1.model = go.Model.fromJson(json);

Button.ClientEvent:
var dataTree = this.get("#dtSystems");
var NodeType = dataTree.get("currentNode.bindingConfig.name");
var bg = "";
var id = "";
if(NodeType == "system"){
	bg = view.id("dsSystems").getData("!CURRENT_SYSTEM").get("background");
	id = view.id("dsSystems").getData("!CURRENT_SYSTEM").get("oid");
	
}
else if(NodeType == "feature"){
	bg = view.id("dsSystems").getData("!CURRENT_FEATURE").get("background");
	id = view.id("dsSystems").getData("!CURRENT_FEATURE").get("oid");
}

var arr = new Array();
var cur = view.id("dsLru").get("data:[@.get('id')=='" + id + "']");
arr.push(
	{"key":-2, "category":"BG", "loc":"0 0", "source":bg}
);
if(cur.length != 0){
	for (var i = 0; i < cur.length; i++) {
		arr.push({
			category: "LRU",
			text: cur[i].get("text"),
			source: cur[i].get("source"),
			loc: cur[i].get("loc"),
			key: cur[i].get("ke")
		});
	}
}
var linearr = new Array();
var line = view.id("dsLine").get("data:[@.get('id')=='" + id + "']");
if(line.length != 0){
	for (var i = 0; i < line.length; i++) {
		var points = line[i].get("points").split(",");
		var parr =  [];
		for(var j = 0; j < points.length; j ++){
			var point = parseFloat(points[j]);
			parr.push(point);
		}
		linearr.push({
			category: line[i].get("line"),
			to: line[i].get("tol"),
			from: line[i].get("froml"),
			toPort: line[i].get("toport"),
			fromPort: line[i].get("fromport"),
			points:parr
		});
		
	}
}
var json = { "class": "go.GraphLinksModel",
  			 "linkFromPortIdProperty": "fromPort",
  			 "linkToPortIdProperty": "toPort",
  			 "nodeDataArray": arr,
  			 "linkDataArray": linearr
			};
myDiagram.model = go.Model.fromJson(json);

var arr = myDiagram1.model.nodeDataArray;
var cur = view.id("dsRoad").getData("#");
var fault = view.id("dsRoad").getData("#.fault");
var farr = fault.toArray();
for(var i = 0; i < farr.length; i++){
	farr[i].remove();
}
for (var i = 0; i < arr.length; i++){
	fault.insert({
		roadid:{roadid:cur.get("roadid")},
		event:arr[i].fault,
		classes:arr[i].name,
		road:arr[i].key,
		origin:arr[i].loc
	});
}
view.id("uaRoad").execute();

