DataTree.ClientEvent:
var system = view.get("@Systems");
var feature = view.get("@Feature");
var LRU = view.get("@Physics");
dorado.DataPath.registerInterceptor("CURRENT_SYSTEM", function(data) {
		var entity = self.get("currentNode.data");
		return (entity && entity.dataType == system) ? entity : null;
	}, function(dataType) {
		return system;
	});
	dorado.DataPath.registerInterceptor("CURRENT_FEATURE", function(data) {
		var entity = self.get("currentNode.data");
		return (entity && entity.dataType == feature) ? entity : null;
	}, function(dataType) {
		return feature;
	});
dorado.DataPath.registerInterceptor("CURRENT_LRU", function(data) {
		var entity = self.get("currentNode.data");
		return (entity && entity.dataType == LRU) ? entity : null;
	}, function(dataType) {
		return LRU;
	});

var dataTree = this.get("#dtSystems");
var NodeType = dataTree.get("currentNode.bindingConfig.name");
var bg = "";
var id = "";
if(NodeType == "system"){
	bg = view.id("dsSystems").getData("!CURRENT_SYSTEM").get("background");
	id = view.id("dsSystems").getData("!CURRENT_SYSTEM").get("oid");
	var name = dataTree.get("currentNode.data").get("system");
	
}
else if(NodeType == "feature"){
	bg = view.id("dsSystems").getData("!CURRENT_FEATURE").get("background");
	id = view.id("dsSystems").getData("!CURRENT_FEATURE").get("oid");
	var name = dataTree.get("currentNode.data").get("feature");
}

var arr = new Array();
var cur = view.id("dsLru").get("data:[@.get('id')=='" + id + "']");
arr.push(
	{"key":-2, "category":"BG", "loc":"0 0", "source":bg}
);
if(cur.length != 0){
	for (var i = 0; i < cur.length; i++) {
		arr.push({
			category: "LRU",
			text: cur[i].get("text"),
			source: cur[i].get("source"),
			loc: cur[i].get("loc"),
			key: cur[i].get("ke")
		});
	}
}
var linearr = new Array();
var line = view.id("dsLine").get("data:[@.get('id')=='" + id + "']");
if(line.length != 0){
	for (var i = 0; i < line.length; i++) {
		var points = line[i].get("points").split(",");
		var parr =  [];
		for(var j = 0; j < points.length; j ++){
			var point = parseFloat(points[j]);
			parr.push(point);
		}
		linearr.push({
			category: line[i].get("line"),
			to: line[i].get("tol"),
			from: line[i].get("froml"),
			toPort: line[i].get("toport"),
			fromPort: line[i].get("fromport"),
			points:parr
		});
		
	}
}
var json = { "class": "go.GraphLinksModel",
  			 "linkFromPortIdProperty": "fromPort",
  			 "linkToPortIdProperty": "toPort",
  			 "nodeDataArray": arr,
  			 "linkDataArray": linearr
			};
myDiagram.model = go.Model.fromJson(json);
view.id("dsRoad").set("parameter",{
	id:id
});
view.id("dsRoad").flush();

