var name = view.id("felru").get("value");
myDiagram1.model.addNodeData({ key: num , "fault": fault, "name": name , category: "LRU", "loc": loc });
if(num>0){
	myDiagram1.model.addLinkData({ from: num-1 , to: num });
}
	
